import { useEffect } from "react";
import { useAssignmentsContext } from "../hooks/useAssignmentsContext";

// components
import WorkoutDetails from '../components/AssignmentDetails'
import WorkoutForm from '../components/AssignmentForm'


const Home = () => {
    const {workouts, dispatch} = useAssignmentsContext()

    useEffect(() => {
        const fetchWorkouts = async () => {
            const response = await fetch('/api/workouts')
            const json = await response.json()

            if (response.ok) {
                dispatch({type: 'SET_WORKOUTS', payload: json})
            }
        }

        fetchWorkouts()
    }, [dispatch])

    return (
        <div className="home">
            <div className="workouts">
                {workouts && workouts
                .sort((a, b) => new Date(a.date) - new Date(b.date))
                .map((workout) => (
                    <WorkoutDetails key={workout._id} workout={workout} />
                ))}
            </div>
            <WorkoutForm />
        </div>
    )
}

export default Home
